import React, { useState } from 'react';
import api, { setAuth } from '../api';

export default function Login(){
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [msg, setMsg] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const submit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setMsg('');

    try{
      const r = await api.post('/login', { email, password });
      setAuth(r.data.token);
      localStorage.setItem('token', r.data.token);
      setMsg('Logged in successfully!');
      setTimeout(() => {
        window.location.href = '/tickets';
      }, 1000);
    }catch(err){ 
      setMsg(err?.response?.data?.error?.code || 'Login failed. Please try again.'); 
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container container-sm">
      <div className="page-header text-center">
        <h1 className="page-title">Welcome Back</h1>
        <p className="page-subtitle">Sign in to your account to continue</p>
      </div>

      <div className="card">
        <div className="card-body">
          <form onSubmit={submit}>
            <div className="form-group">
              <label htmlFor="email" className="form-label">Email Address</label>
              <input 
                id="email"
                type="email"
                className="form-input"
                value={email} 
                onChange={e=>setEmail(e.target.value)} 
                placeholder="Enter your email" 
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="password" className="form-label">Password</label>
              <input 
                id="password"
                type="password" 
                className="form-input"
                value={password} 
                onChange={e=>setPassword(e.target.value)} 
                placeholder="Enter your password" 
                required
              />
            </div>

            <button 
              type="submit" 
              className="btn btn-primary w-full"
              disabled={isLoading}
            >
              {isLoading ? (
                <>
                  <div className="spinner"></div>
                  Signing in...
                </>
              ) : (
                'Sign In'
              )}
            </button>

            {msg && (
              <div className={`alert ${msg.includes('successfully') ? 'alert-success' : 'alert-error'} mt-3`}>
                {msg}
              </div>
            )}
          </form>
        </div>
      </div>
    </div>
  );
}
