import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../api';

export default function TicketDetail(){
  const { id } = useParams();
  const [data, setData] = useState(null);
  const [msg, setMsg] = useState('');
  const [comment, setComment] = useState('');
  useEffect(()=>{ load(); },[]);
  const load = async ()=>{ const r = await api.get(`/tickets/${id}`); setData(r.data); };
  const addComment = async ()=>{ try{ await api.post(`/tickets/${id}/comments`, { message: comment }); setComment(''); load(); }catch(e){ setMsg('error'); } };
  if (!data) return <div>Loading...</div>;
  return (
    <div style={{margin:20}}>
      <h2>{data.ticket.title}</h2>
      <p>{data.ticket.description}</p>
      <p>Status: {data.ticket.status} | Priority: {data.ticket.priority} | SLA: {new Date(data.ticket.sla_due).toLocaleString()}</p>
      <h3>Comments</h3>
      <ul>{data.comments.map(c=> <li key={c._id}><b>{c.user?.email}</b>: {c.message}</li>)}</ul>
      <textarea value={comment} onChange={e=>setComment(e.target.value)} />
      <br />
      <button onClick={addComment}>Add Comment</button>
      <div>{msg}</div>
    </div>
  );
}
