import React, { useState } from 'react';
import api from '../api';
import { v4 as uuidv4 } from 'uuid';

export default function NewTicket(){
  const [title,setTitle]=useState('');
  const [description,setDescription]=useState('');
  const [priority,setPriority]=useState('medium');
  const submit = async (e)=>{
    e.preventDefault();
    try{
      const key = uuidv4();
      const r = await api.post('/tickets', { title, description, priority }, { headers: { 'Idempotency-Key': key } });
      alert('Created: '+r.data._id);
      window.location.href = '/tickets';
    }catch(e){ alert(e?.response?.data?.error?.code || 'Error'); }
  };
  return (
    <form onSubmit={submit} style={{maxWidth:600, margin:20}}>
      <h2>New Ticket</h2>
      <input value={title} onChange={e=>setTitle(e.target.value)} placeholder="title" />
      <textarea value={description} onChange={e=>setDescription(e.target.value)} placeholder="description"></textarea>
      <select value={priority} onChange={e=>setPriority(e.target.value)}>
        <option value="low">Low</option>
        <option value="medium">Medium</option>
        <option value="high">High</option>
      </select>
      <button type="submit">Create</button>
    </form>
  );
}
